class DbHelper {
  late Database _db;
  final DBController dbController = Get.find(tag: 'dbController');
  String stringFocus(
      String current, String next, int index, int subStringLength) {
    int end = next.length - 1;
    int start = 0;
    if (index - 51 > start) {
      start = index - 51;
    }
    if (end > 51) {
      end = 50;
    }
    String displayStr = '';
    displayStr = current.substring(start, index + subStringLength) +
        next.substring(0, end);
    return displayStr;
  }

  Future<List<Map>> getFromExistingDB(String searchText) async {
    bool value = dbController.articledb.isOpen;
    if (value) {
      print("Database is open");
      List<Map> list = await dbController.articledb.rawQuery(
          "SELECT * FROM article WHERE article MATCH \'$searchText\'");

      List<String> allSearchList = [];
      List<Map> searchResults = [];

      for (int i = 0; i < list.length; i++) {
        int len = 0;
        String allText = list[i]['content'].toString();
        int index = 0;
        int oldIndex = 0;
        bool flag = true;
        while (flag) {
          index = allText.indexOf(searchText);
          if (index >= 0) {
            len = allText.length;
            String current =
                allText.substring(oldIndex, index + searchText.length);
            String next = allText.substring(index + searchText.length, len - 1);
            String focusStr =
                stringFocus(current, next, index, searchText.length);

            String findedText = allText.substring(oldIndex, index);
            findedText =
                focusStr.replaceAll(searchText, "<mark>$searchText</mark> ");
            var idAndshortValue = {
              'fullText': list[i]['content'].toString(),
              'displayStr': findedText
            };
            print(index);
            allText = allText.substring(index + searchText.length, len - 1);

            searchResults.add(idAndshortValue);
          } else {
            flag = false;
          }
        }
      }

      return searchResults;
    } else {
      print("Database is not open");

      return [];
    }
  }

  Future<void> initTheDB() async {
    io.Directory applicationDirectory =
        await getApplicationDocumentsDirectory();

    String dbPathMovie = path.join(applicationDirectory.path, "movies.db");

    bool dbExistsMovie = await io.File(dbPathMovie).exists();
//Add not ! this check
    if (dbExistsMovie) {
      print("DB not exists");
      // Copy from asset
      ByteData data = await rootBundle.load(path.join("assets", "movies.db"));
      List<int> bytes =
          data.buffer.asUint8List(data.offsetInBytes, data.lengthInBytes);

      // Write and flush the bytes written
      await io.File(dbPathMovie).writeAsBytes(bytes, flush: true);
    } else {
      print("DB exists");
    }

    _db = await openDatabase(dbPathMovie);
    List<Map> list = await _db.rawQuery(
        "SELECT title, overview, poster, year FROM movies WHERE movies MATCH \'usama*\'");
    String overview = list[0]['overview'];
    overview = overview + overview;
    print(overview.length);

    await _db
        .rawQuery('CREATE TEMP TABLE SearchResult ( id INTEGER ,content )');
  }

  fts() async {
    List<Map> list = await _db
        .rawQuery("CREATE VIRTUAL TABLE articles USING FTS3(id,content);");
    print(list.length);
  }

